import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Gem(props) {
	return (
		<button className='square' onClick={props.onClick}>
			{props.value}
		</button>
	);
}

class Board extends React.Component {
	renderGem(i) {
		return (
			<Gem
				value={this.props.squares[i]}
				onClick={() => this.props.onClick(i)}
			/>
		);
	}

	render() {
		const board = [];
		
		for(let i=0; i<this.props.rows; i++) {
			const squares = [];
			for(let j=0; j<this.props.cols; j++) squares.push(<div key={i*this.props.rows+j}>{this.renderGem(i*this.props.rows+j)}</div>);
			board.push(<div className="board-row" style={{width: (this.props.cols*34)+'px'}} key={i}>{squares}</div>);
		}
		
		return (
			<div>
				{board}
			</div>
		); 
	}
}

class Game extends React.Component {
	constructor(props) {
		const rows = 8;
		const cols = 6; 
		
		super(props);
		this.state = {
			rows: rows,
			cols: cols,
			history: [
				{
					squares: Array(rows*cols).fill(null), 
					col: null, 
					row: null, 
				}
			],
			stepNumber: 0,
			xIsNext: true, 
		};
	}

	handleClick(i) {
		const history = this.state.history.slice(0, this.state.stepNumber + 1);
		const current = history[history.length - 1];
		const squares = current.squares.slice();
		if (calculateWinner(squares) || squares[i]) {
			return;
		}
		squares[i] = this.state.xIsNext ? "X" : "O";
		
		this.setState({
			history: history.concat([
				{
					squares: squares,
				}
			]),
			stepNumber: history.length,
			xIsNext: !this.state.xIsNext
		});
	}

	render() {
		const history = this.state.history;
		const current = history[this.state.stepNumber];
		const winner = calculateWinner(current.squares);

		let status;
		if (winner) {
			status = "Winner: " + winner;
		} else {
			status = "Next player: " + (this.state.xIsNext ? "X" : "O");
		}

		return (
			<div className="game">
				<div className="game-info">
					<div>{status}</div>
				</div>
				<div className="game-board">
					<Board
						rows={this.state.rows}
						cols={this.state.cols}
						squares={current.squares}
						onClick={i => this.handleClick(i)}
					/>
				</div>
			</div>
		);
	}
}

// ========================================

ReactDOM.render(<Game />, document.getElementById("root"));

function calculateWinner(squares) {
	const lines = [
		[0, 1, 2],
		[3, 4, 5],
		[6, 7, 8],
		[0, 3, 6],
		[1, 4, 7],
		[2, 5, 8],
		[0, 4, 8],
		[2, 4, 6]
	];
	for (let i = 0; i < lines.length; i++) {
		const [a, b, c] = lines[i];
		if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
			return squares[a];
		}
	}
	return null;
}
